


function login() {
    var usw = document.getElementById("usw").value;
    var psw = document.getElementById("psw").value;
    var xhr = new XMLHttpRequest();
    xhr.open("GET", "http://localhost:20775?type=login&usw=" + usw + "&psw=" + psw, true);
    xhr.send();
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
            var result = xhr.responseText;
            alert(result)
        }
    }
}

function reg() {
    var usw = document.getElementById("usw").value;
    var psw = document.getElementById("psw").value;
    var mail = document.getElementById("mail").value;
    var xhr = new XMLHttpRequest();
    xhr.open("GET", "http://localhost:20775?type=reg&usw=" + usw + "&psw=" + psw + "&mail=" + mail, true);
    xhr.send();
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
            var result = xhr.responseText;
            alert(result)
        }
    }
}

function stringToBase64(str) {
    var base64 = btoa(unescape(encodeURIComponent(str)));
    return base64;
}
function base64ToString(base64) {
    var str = decodeURIComponent(escape(atob(base64)));
    return str;
}
